let chart=new Chartist.Line(".ct-chart",{labels:["1月","2月","3月","4月","5月","6月","7月","8月","9月","10月","11月","12月"],series:[[12,9,7,8,5,4,6,2,3,3,4,6],[4,5,3,7,3,5,5,3,4,4,5,5],[5,3,4,5,6,3,3,4,5,6,3,4],[3,4,5,6,7,6,4,5,6,7,6,3]]},{low:0});let seq=0,delays=40,durations=300;chart.on("created",function(){seq=0});chart.on("draw",function(a){seq++;if(a.type==="line"){a.element.animate({opacity:{begin:seq*delays+1000,dur:durations,from:0,to:1}})}else{if(a.type==="label"&&a.axis==="x"){a.element.animate({y:{begin:seq*delays,dur:durations,from:a.y+100,to:a.y,easing:"easeOutQuart"}})}else{if(a.type==="label"&&a.axis==="y"){a.element.animate({x:{begin:seq*delays,dur:durations,from:a.x-100,to:a.x,easing:"easeOutQuart"}})}else{if(a.type==="point"){a.element.animate({x1:{begin:seq*delays,dur:durations,from:a.x-10,to:a.x,easing:"easeOutQuart"},x2:{begin:seq*delays,dur:durations,from:a.x-10,to:a.x,easing:"easeOutQuart"},opacity:{begin:seq*delays,dur:durations,from:0,to:1,easing:"easeOutQuart"}})}else{if(a.type==="grid"){let pos1Animation={begin:seq*delays,dur:durations,from:a[a.axis.units.pos+"1"]-30,to:a[a.axis.units.pos+"1"],easing:"easeOutQuart"};let pos2Animation={begin:seq*delays,dur:durations,from:a[a.axis.units.pos+"2"]-100,to:a[a.axis.units.pos+"2"],easing:"easeOutQuart"};let animations={};animations[a.axis.units.pos+"1"]=pos1Animation;animations[a.axis.units.pos+"2"]=pos2Animation;animations.opacity={begin:seq*delays,dur:durations,from:0,to:1,easing:"easeOutQuart"};a.element.animate(animations)}}}}}});chart.on("created",function(){if(window.__exampleAnimateTimeout){clearTimeout(window.__exampleAnimateTimeout);window.__exampleAnimateTimeout=null}window.__exampleAnimateTimeout=setTimeout(chart.update.bind(chart),12000)});